mapper.select:
SELECT aa.id AS id, aa.fault_code AS faultCode, bb.fault_type AS faultType, bb.fault_phenomenon AS faultPhenomenon, aa.fault_analysis AS faultAnalysis, aa.solution AS solution, aa.create_time AS createTime FROM fault_analysis_report aa LEFT JOIN fault bb ON aa.fault_code = bb.`code` WHERE aa.del_flag = 0 AND bb.del_flag = 0
AND aa.fault_code = #{param.code}
AND bb.station_code = #{param.stationCode}
AND bb.fault_phenomenon like '%${param.faultPhenomenon}%'
AND aa.create_time >= #{param.dayStart} and aa.create_time < #{param.dayEnd}
ORDER BY aa.create_time DESC
