SELECT wl.*, su.realname AS succeedName, sur.realname AS submitName, sur.org_name AS submitOrgName, sur.avatar AS avatar FROM t_work_log wl LEFT JOIN sys_user su ON wl.succeed_id = su.id LEFT JOIN sys_user sur ON wl.submit_id = sur.id WHERE wl.del_flag = '0'
AND wl.submit_id = #{param.submitId}
AND wl.submit_time >= #{param.dayStart} and wl.submit_time < #{param.dayEnd}
AND wl.check_status = #{param.checkStatus}
AND wl.confirm_status = #{param.confirmStatus}
AND wl.org_id = #{param.departId}
AND wl.org_id IN
#{id}
ORDER BY wl.create_time DESC

SELECT wl.*, su.realname AS succeedName, sur.realname AS submitName, sur.org_name AS submitOrgName, sur.avatar AS avatar FROM t_work_log wl LEFT JOIN sys_user su ON wl.succeed_id = su.id LEFT JOIN sys_user sur ON wl.submit_id = sur.id WHERE wl.del_flag = '0'
AND wl.submit_id = #{param.submitId}
AND wl.submit_time >= #{param.dayStart} and wl.submit_time < #{param.dayEnd}
AND wl.check_status = #{param.checkStatus}
AND wl.confirm_status = #{param.confirmStatus}
AND wl.org_id IN
#{id}
ORDER BY wl.create_time DESC

        SELECT
	        wl.assort_num AS assortNum
