mapper.select:
select tfi.*, ri.robot_name, ri.line_code, ri.station_code from task_finish_info tfi join robot_info ri on tfi.robot_id = ri.robot_id
ri.line_code = #{condition.lineCode}
and ri.station_code = #{condition.stationCode}
and tfi.finish_state like concat('%',#{condition.taskStatus},'%')
and DATE_FORMAT(tfi.start_time, '%Y-%m-%d') >= DATE_FORMAT(#{condition.startTime} , '%Y-%m-%d') and DATE_FORMAT(tfi.end_time, '%Y-%m-%d') <= DATE_FORMAT(#{condition.endTime}, '%Y-%m-%d')
order by tfi.start_time desc,tfi.end_time desc
