mapper.select:
select tfi.*,
ri.line_code,
ri.station_code
from task_finish_info tfi
join robot_info ri on tfi.robot_id = ri.robot_id


        ri.line_code = #{condition.lineCode}


        and ri.station_code = #{condition.stationCode}


        and tfi.finish_state like concat('%',#{condition.taskStatus},'%')


        and tfi.start_time >= #{condition.startTime} and tfi.end_time <= #{condition.endTime}
