mapper.select:
select sr.user_id as userId, su.realname as realName, sr.item_name as itemName, IF (sr.item_name = '夜', 1, 0) as status FROM sys_user su INNER JOIN schedule_record sr on su.id = sr.user_id WHERE sr.date = #{date}
and su.org_id = #{orgId}
AND sr.item_name != '休' AND sr.del_flag = 0

select sr.id as id, sr.user_id as userId, sr.date as date, su.realname as userName, sr.item_id as itemId, si.name as itemName, si.color as color from schedule_record sr inner join schedule_item si on sr.item_id = si.id inner join sys_user su on sr.user_id=su.id where DATE_FORMAT(sr.date , '%Y-%m') = #{date} and sr.del_flag = 0 and si.del_flag=0
and su.org_id=#{orgId}
order by sr.date, sr.item_id

select su.avatar as avatar, su.username as username, su.realname as realname, su.id as id, su.org_name as orgName, su.post as post, su.salary_code as salaryCode, su.permit_code as permitCode, sr.item_id as status, su.org_id as orgId, su.org_code as orgCode from schedule_record sr inner join sys_user su on sr.user_id=su.id where TO_DAYS(sr.date) = TO_DAYS(#{day}) and sr.del_flag = 0
and su.org_id=#{orgId}
order by sr.date, sr.item_id

select sr.id as id, sr.user_id as userId, sr.date as date, su.realname as userName, sr.item_id as itemId, si.name as itemName, si.color as color from schedule_record sr inner join schedule_item si on sr.item_id = si.id inner join sys_user su on sr.user_id=su.id where DATE_FORMAT(sr.date , '%Y-%m-%d') = #{date}
and su.id in
#{userId}
and sr.del_flag = 0 and si.del_flag=0 order by sr.date, sr.item_id

select COUNT(a.id) from schedule_record a left join sys_user b on a.user_id=b.id
where a.del_flag=0 and a.item_name!='休'

    and b.org_id in

        #{orgId}



    and a.date = #{today}

select su.avatar as avatar, su.username as username, su.realname as realname, su.id as id, su.org_name as orgName, su.post as post, su.salary_code as salaryCode, su.permit_code as permitCode, sr.item_id as status, su.org_id as orgId, su.org_code as orgCode FROM sys_user su INNER JOIN schedule_record sr on su.id = sr.user_id WHERE sr.date = #{date}
and su.org_id in
#{orgId}
AND sr.item_name != '休' AND sr.del_flag = 0

select *
from sys_user su
where su.org_id = #{orgCode}

select DISTINCT sd.org_code from sys_depart sd left join work_area_org wao on wao.org_code = sd.org_code left join work_area wa on wa.`code` = wao.work_area_code left join work_area_line wal on wal.work_area_code = wa.`code`
sd.del_flag = 0 and wao.del_flag= 0 and wa.del_flag = 0 and wal.del_flag = 0
and wal.line_code in
#{code}
and wa.major_code in
#{code}

SELECT sr.user_id AS userId, su.realname AS realName, su.username, su.permit_code, su.salary_code FROM schedule_record sr INNER JOIN schedule_item si ON sr.item_id = si.id INNER JOIN sys_user su ON sr.user_id = su.id left join sys_user_depart sud on sud.user_id = su.id WHERE DATE_FORMAT( sr.date, '%Y-%m-%d' ) =DATE_FORMAT( #{date},'%Y-%m-%d') AND sr.del_flag = 0 AND si.del_flag = 0
and su.id in
#{user}
and su.dep_id = #{orgId}
ORDER BY sr.date, sr.item_id
