mapper.select:
SELECT id, train_task_id, exam_paper_id, user_id,exam_classify, is_makeup,makeup_time from bd_train_makeup_exam_record
is_makeup = 1 and user_id = #{userId} and train_task_id = #{trainTaskId}
and exam_paper_id = #{examPaperId}

SELECT t1.id as id, t2.id as examPaperId, t2.name as paperName, t2.number as number, IFNULL(t1.exam_classify,1) as examClassify, t2.passscore as passcode, t2.score as paperScore, t3.exam_plan_time as examTime, t1.answer_time as answerTime, t1.submit_time as submitTime, t1.usetime as usetime, t1.is_release as isRelease, t1.score as score, t3.id as trainTaskId, t1.is_pass as isPass FROM bd_train_task t3 LEFT JOIN bd_train_task_user t4 ON t3.id = t4.train_task_id LEFT JOIN bd_exam_record t1 ON t1.train_task_id = t3.id and t4.user_id = t1.user_id LEFT JOIN bd_exam_paper t2 ON t3.exam_paper_id = t2.id
t3.id = #{trainTaskId}
and t4.user_id = #{userId}
and t1.exam_classify = #{examClassify}
and t1.id = #{id}

SELECT a.* FROM ( SELECT t1.id as id, t2.id as examPaperId, t2.name as paperName, t2.number as number, IFNULL(t1.exam_classify,1) as examClassify, t2.passscore as passcode, t2.score as paperScore, t3.exam_plan_time as examTime, t1.answer_time as answerTime, t1.submit_time as submitTime, t1.usetime as usetime, t1.is_release as isRelease, t1.score as score, t3.id as trainTaskId, t1.is_pass as isPass FROM bd_train_task t3 LEFT JOIN bd_train_task_user t4 ON t3.id = t4.train_task_id LEFT JOIN bd_exam_record t1 ON t1.train_task_id = t3.id and t4.user_id = t1.user_id LEFT JOIN bd_exam_paper t2 ON t3.exam_paper_id = t2.id
t3.id = #{trainTaskId}
and t4.user_id = #{userId}
)as a
a.examClassify = #{examClassify}

SELECT t1.id as id, t2.id as examPaperId, t3.id as trainTaskId, t1.exam_time as testTime, t1.answer_time as answerTime, t1.submit_time as submitTime, t1.exam_classify as examClassify, t1.score as score, t1.usetime as usetime, t1.is_pass as isPass, t2.name as paperName, t2.score as paperScore, t2.passscore as passcode, t3.exam_task_name as examTaskName, t3.exam_classify as examinationType, t3.task_state as task_state, t3.start_exam_time as examPlanTime FROM bd_exam_record t1 LEFT JOIN bd_exam_paper t2 ON t1.exam_paper_id = t2.id LEFT JOIN bd_train_task t3 ON t1.train_task_id = t3.id
t1.is_release >=3 ANd t1.is_release <=4
AND t2.name LIKE CONCAT ('%', #{condition.paperName}, '%')
AND t3.exam_classify = #{condition.examinationType}
AND t1.is_pass = #{condition.isPass}
AND t1.user_id = #{condition.userId}
AND DATE_FORMAT(t1.exam_time, '%Y-%m-%d') = DATE_FORMAT(#{condition.testTime}, '%Y-%m-%d')
order by t1.answer_time desc

SELECT t1.id as id, t2.id as taskId, t2.exam_plan_time as examination, t2.start_exam_time as examTime, t2.exam_task_name as examTaskName, t1.name as name, t1.number as number, t1.score as score, t1.passscore as passscore, t2.task_state as state, t2.exam_classify as examinationType, t2.make_up_state as makeUpState, t2.exam_validity_period as examValidityPeriod FROM bd_exam_paper t1 LEFT JOIN bd_train_task t2 ON t2. exam_paper_id = t1.id
t1.id = #{id}
and t2.exam_classify = #{exemplify}
and t2.task_state = #{state} and t2.id = #{taskId}

SELECT t1.id as recordId, t1.score as score, t1.exam_time as examTime, t1.submit_time as submitTime, t1.usetime as useTime, t1.is_pass as isPass, t1.exam_paper_id as examPaperId FROM bd_exam_record t1
t1.user_id = #{id} and t1.train_task_id =#{traintaskid}
and t1.exam_classify = #{examClassify}
and t1.exam_paper_id = #{examPaperId}

SELECT t1.id as recordId, t1.score as score, t1.answer_time as examTime, t1.submit_time as submitTime, t1.usetime as useTime, t1.is_pass as isPass, t1.exam_paper_id as examPaperId, t1.exam_classify as makeUpState, t1.is_release as isRelease, t1.exam_state as examState FROM bd_exam_record t1
t1.user_id = #{id} and t1.train_task_id =#{traintaskid}
and t1.exam_classify = #{examClassify}
and t1.exam_paper_id = #{examPaperId}
order by t1.exam_time desc LIMIT 1

SELECT user_id as id  from bd_exam_record


      exam_paper_id = #{id}


       and train_task_id = #{takeId}

SELECT tt.exam_task_name as paperName, ep.passscore as passcode, ep.number, tt.start_exam_time as examTime, ep.score as paperScore,(select task_state from bd_train_task where id=#{trainTaskId})as isRelease,tt.exam_plan_time as examPlanTime,tt.exam_classify as examClassify, (SELECT COUNT(er.is_pass = 1 AND er.exam_classify=1 OR NULL )FROM bd_exam_record as er WHERE er.train_task_id =#{trainTaskId} )as passesNumber, ( SELECT `name` FROM bd_team WHERE id = tt.task_team_id ) AS taskTeamName, ifnull( ( SELECT COUNT(ttu.user_id) FROM bd_train_task_user AS ttu WHERE train_task_id =#{trainTaskId} ), 0 ) AS referenceNumber, ifnull((SELECT COUNT(er.user_id AND er.exam_classify=1 OR NULL) FROM bd_exam_record AS er WHERE train_task_id=#{trainTaskId}),0)AS actualReferenceNumber FROM bd_train_task as tt LEFT JOIN bd_exam_paper ep ON tt.exam_paper_id = ep.id LEFT JOIN bd_exam_record er on er.train_task_id = tt.id WHERE
tt.id=#{trainTaskId}
AND ep.id =#{examPaperId}
GROUP BY ep. NAME

   select  t1.id,t1.exam_classify,t1.score,t1.is_pass
   from bd_exam_record t1
   LEFT JOIN bd_exam_paper ep ON ep.id = t1.exam_paper_id
   where
   ep.id =#{examPaperId}

   and t1.exam_classify = #{examClassify}


    and t1.user_id = #{userId}


and t1.train_task_id = #{taskId}

SELECT * from (SELECT t1.id as id, t2.id as examPaperId, t3.id as trainTaskId, IFNULL(t1.exam_classify,1) as examClassify, t1.score as score, t1.is_pass as isPass, t1.answer_time as answerTime, t1.submit_time as submitTime, IFNULL(t1.is_release, case when t3.task_state in (1,2,3) then 0 when t3.task_state = 4 then 1 when t3.task_state = 5 then 2 else 3 end) as isRelease, t2.name as paperName, t2.score as paperScore, t2.passscore as passcode, t3.exam_task_name as examTaskName, t3.exam_classify as examinationType, t3.task_state as taskState, t3.start_exam_time as examPlanTime, t3.exam_plan_time as examination, t4.user_id as userId, ifnull(t1.exam_state,0) as examState, t1.usetime, t1.update_time FROM bd_train_task t3 LEFT JOIN bd_train_task_user t4 ON t3.id = t4.train_task_id LEFT JOIN bd_exam_record t1 on t1.train_task_id =t3.id and t4.user_id = t1.user_id LEFT JOIN bd_exam_paper t2 ON t3.exam_paper_id = t2.id where t3.exam_status =1 and t3.task_state != 0
AND t4.user_id = #{condition.userId}
) as a where 1=1
AND a.paperName LIKE CONCAT ('%', #{condition.paperName}, '%')
AND a.examinationType = #{condition.examinationType}
AND a.isPass = #{condition.isPass}
AND a.examTaskName LIKE CONCAT ('%', #{condition.examTaskName}, '%')
and a.examState = #{condition.examState}
and a.isRelease =#{condition.examTaskState}
AND a.isRelease in(2,4)
order by a.examination desc,a.trainTaskId asc
