mapper.select:
SELECT wl.*, sur.org_id as submitOrgId, su.realname AS succeedName, sur.realname AS submitName, sur.org_name AS submitOrgName, sur.avatar AS avatar, sd.depart_name AS OrgName FROM t_work_log wl LEFT JOIN sys_user su ON wl.succeed_id = su.id LEFT JOIN sys_user sur ON wl.submit_id = sur.id LEFT JOIN sys_depart sd ON sd.id = wl.org_id WHERE wl.del_flag = 0
and wl.id in
#{id}
AND (wl.submit_id = #{param.submitId} OR find_in_set (#{param.successorId} ,wl.succeed_id))
and wl.submit_id = #{param.submitId}
and DATE_FORMAT(wl.submit_time ,'%Y-%m-%d') = DATE_FORMAT(#{param.day},'%Y-%m-%d')
AND wl.check_status = #{param.checkStatus}
AND wl.confirm_status = #{param.confirmStatus}
AND wl.org_id = #{param.departId}
AND wl.org_id IN
#{id}
AND sd.org_code = #{param.departCode}
AND DATE_FORMAT(wl.submit_time, "%Y-%m-%d") >= DATE_FORMAT(#{param.startDate}, "%Y-%m-%d") AND DATE_FORMAT(wl.submit_time, "%Y-%m-%d") <= DATE_FORMAT(#{param.endDate}, "%Y-%m-%d")
AND wl.status = #{param.status}
ORDER BY wl.create_time DESC,id DESC

SELECT wl.*, su.realname AS succeedName, sur.realname AS submitName, sur.org_name AS submitOrgName, sur.org_id as submitOrgId, sur.avatar AS avatar FROM t_work_log wl LEFT JOIN sys_user su ON wl.succeed_id = su.id LEFT JOIN sys_user sur ON wl.submit_id = sur.id WHERE wl.del_flag = '0'
and wl.id in
#{id}
AND wl.submit_id = #{param.submitId}
and DATE_FORMAT(wl.submit_time ,'%Y-%m-%d') = DATE_FORMAT(#{param.day},'%Y-%m-%d')
AND wl.check_status = #{param.checkStatus}
AND wl.confirm_status = #{param.confirmStatus}
AND wl.org_id IN
#{id}
ORDER BY wl.create_time DESC

        SELECT
	        wl.assort_num AS assortNum
        FROM
            t_work_log wl
	        left join sys_user su on su.id = wl.create_by
        WHERE
            wl.create_time >= #{startTime}
            and wl.create_time <= #{entTime}
	        and wl.del_flag = 0

                and su.realname = #{userName}

select staion_code, position_code from cs_station_position where del_flag = 0

    and position_code in

        #{item}

SELECT t1.id
FROM t_work_log t1
INNER JOIN (
    SELECT log_time, org_id
    FROM t_work_log
    WHERE id = #{id}
) t2 ON t1.log_time = t2.log_time AND t1.org_id = t2.org_id;
