mapper.select:
SELECT
from emergency_materials t1 LEFT JOIN emergency_materials_usage t3 ON t1.id = t3.materials_id
t1.del_flag = 0 and t3.del_flag = 0
and t1.category_code = #{condition.categoryCode}
and t1.materials_name LIKE CONCAT ('%', #{condition.materialsName}, '%')
and DATE_FORMAT(t3.use_date, '%Y-%m-%d') >= DATE_FORMAT(#{condition.startTime}, '%Y-%m-%d')
and DATE_FORMAT(t3.use_date, '%Y-%m-%d') <= DATE_FORMAT(#{condition.endTime}, '%Y-%m-%d')
and t1.materials_code = #{condition.materialsCode}
and t3.id = #{condition.id}
AND t1.primary_org in
#{item}
order by t3.create_time desc,t3.update_time desc
