mapper.select:
select *
from cs_work where 1 = 1

    and major_code = #{majorCode}

SELECT id
FROM fault_knowledge_base
        WHERE 1 = 1 and del_flag = 0 and status = 1

    and MATCH (
                      `fault_phenomenon`,
                      `fault_reason`,
                      `solution`
                      ) AGAINST (#{matchName} IN BOOLEAN MODE)


    and fault_phenomenon like concat('%'
      , #{faultPhenomenon}
      , '%')



    and major_code = #{majorCode}


    and system_code = #{systemCode}


and knowledge_base_type_code = #{knowledgeBaseTypeCode}


    and device_type_code in (
    select device.device_type_code  from device where code in

            #{item}

    )

select *,
        MATCH (
        `fault_phenomenon`,
        `fault_reason`,
        `solution`
        ) AGAINST (
       #{condition.matchName} IN BOOLEAN MODE) as scode
from
        fault_knowledge_base
        WHERE 1=1
          and  del_flag = 0
          and status = 1

    and MATCH (
            `fault_phenomenon`
      ,
            `fault_reason`
      ,
            `solution`
            ) AGAINST (#{condition.matchName} IN BOOLEAN MODE)


    and fault_phenomenon like concat('%'
      , #{condition.faultPhenomenon}
      , '%')


    and major_code = #{condition.majorCode}


    and system_code = #{condition.systemCode}


    and method like concat('%'
      , #{condition.method}
      , '%')



    and fault_reason like concat('%'
      , #{condition.faultReason}
      , '%')



    and solution like concat('%'
      , #{condition.solution}
      , '%')


    and id in

        #{item}



    and device_type_code in (
    select device.device_type_code  from device where code in

        #{item}

    )


    and device_type_code = #{condition.deviceTypeCode}


    and knowledge_base_type_code = #{condition.knowledgeBaseTypeCode}

select major_name from cs_major
major_code in
#{item}

select system_name from cs_subsystem
system_code in
#{item}

SELECT DISTINCT su.username FROM sys_user_role sur JOIN sys_user su ON sur.user_id = su.id JOIN sys_role sr ON sur.role_id = sr.id LEFT join cs_user_major cum ON cum.user_id = su.id LEFT JOIN cs_major cm ON cum.major_id = cm.id LEFT JOIN cs_user_subsystem cus on cus.user_id = su.id LEFT JOIN cs_subsystem cs ON cs.id = cus.system_id LEFT JOIN cs_user_station us on us.user_id = su.id LEFT JOIN cs_station c on us.station_id = c.id where su.del_flag = 0
and sr.role_code in
#{code}
and cm.major_code = #{majorCode}
and cs.system_code = #{subSystemCode}
and c.station_code = #{stationCode}
and su.org_code = #{sysOrgCode}

SELECT id, `code`, major_code, fault_mode_code, sub_system_code, line_code, station_code, fault_applicant, happen_time, station_position_code, (select group_concat(device_code) from fault_device where fault_code = `code`) as deviceCode, ( select group_concat(d.name) from (select device_code from fault_device where fault_code = `code`) a left join device d on d.`code` = a.device_code ) as deviceName, end_time, hang_up_reason, fault_phenomenon, symptoms FROM fault where status = 12
and `code` like concat('%',#{condition.code},'%')
and fault_mode_code = #{condition.faultModeCode}
and (station_code = #{condition.stationCode} or line_code =#{condition.stationCode} or station_position_code =#{condition.stationCode})
and `code` in ((select fault_code from fault_device where device_code like concat('%',#{condition.deviceCode},'%')))
and major_code =#{condition.majorCode}
and sub_system_code =#{condition.subSystemCode}
and `code` not in
#{item}
