mapper.select:
SELECT oar.id, oar.alm_time, oar.state, oar.`level`, oar.alm_text, oar.device_id, oar.create_time, oar.last_alm_time, oar.alm_num, d.`code` AS device_code, d.station_code, d.device_type_code, d.major_code, d.system_code AS sub_system_code, d.line_code FROM on_alm_record oar LEFT JOIN device d ON d.id = oar.device_id
d.system_code = #{condition.subSystemCode}
and d.station_code = #{condition.stationCode}
GROUP BY oar.id Order BY oar.last_alm_time desc,oar.alm_time desc,d.code desc

SELECT oar.id, oar.alm_time, oar.state, oar.`level`, oar.alm_text, oar.device_id, oar.last_alm_time, oar.alm_num, oar.deal_user_id, oar.create_time, CONCAT(IFNULL(cancel_reason,''), ' ', IFNULL(deal_remark,'')) AS deal_remark, oar.deal_date_time, oar.fault_code, d.`code` AS device_code, d.station_code, d.device_type_code, d.major_code, d.system_code AS sub_system_code FROM on_alm_record_history oar LEFT JOIN device d ON d.id = oar.device_id
d.system_code = #{condition.subSystemCode}
and d.station_code = #{condition.stationCode}
and oar.state = #{condition.state}
and DATE(oar.alm_time) BETWEEN #{condition.startAlmTime} AND #{condition.endAlmTime}
GROUP BY oar.id Order BY oar.last_alm_time desc,oar.alm_time desc,d.code desc
