mapper.select:
select a.id,a.status,a.faultCode ,a.fault_phenomenon,a.solution,a.name,a.createTime,a.faultId from (( SELECT far.id, far.status, f.`code` as faultCode , f.fault_phenomenon, far.solution, ft.name, far.create_time, f.id as faultId, ft.`code` as faultTypeCode, far.create_time as createTime, f.sub_system_code FROM fault_analysis_report far LEFT JOIN fault f ON f.CODE = far.fault_code LEFT JOIN fault_type ft on ft.`code` = f.fault_type_code WHERE far.del_flag = 0 and far.approved_result = 0
and far.approved_result = #{condition.approvedResult}
and far.create_by = #{condition.createBy}
) union all ( SELECT far.id, far.STATUS, f.`code` AS faultCode, f.fault_phenomenon, far.solution, ft.NAME, far.create_time, f.id AS faultId, ft.`code` as faultTypeCode, far.create_time as createTime, f.sub_system_code FROM fault_analysis_report far LEFT JOIN fault f ON f.CODE = far.fault_code LEFT JOIN fault_type ft ON ft.`code` = f.fault_type_code WHERE far.del_flag = 0 and far.approved_result = 1
and far.approved_result = #{condition.approvedResult}
))a where 1=1
and a.faultCode LIKE concat('%',#{condition.faultCode},'%')
and a.fault_phenomenon LIKE concat('%',#{condition.faultPhenomenon}',%')
and a.faultTypeCode like concat('%',#{condition.faultTypeCode},'%')
and DATE_FORMAT(a.createTime ,'%Y-%m-%d') >= DATE_FORMAT(#{condition.startTime},'%Y-%m-%d') and DATE_FORMAT(a.createTime ,'%Y-%m-%d') <= DATE_FORMAT(#{condition.endTime},'%Y-%m-%d')
and a.faultId in
#{item}
ORDER BY a.create_time DESC

SELECT f.id, f.`code`, f.major_code, f.fault_mode_code, f.sub_system_code, f.line_code, f.station_code, f.fault_phenomenon, f.fault_applicant, f.happen_time, f.station_position_code, (select group_concat(device_code) from fault_device where fault_code = f.`code`) as deviceCode, ( select group_concat(d.name) from (select device_code from fault_device where fault_code = f.`code`) a left join device d on d.`code` = a.device_code ) as deviceName, f.end_time, f.hang_up_reason FROM fault f where f.status = 12
and f.`code` like concat('%',#{condition.code},'%')
and f.fault_mode_code = #{condition.faultModeCode}
and f.station_code = #{condition.stationCode}
and f.`code` in ((select fault_code from fault_device where device_code like concat('%',#{condition.deviceCode},'%')))
and f.major_code =#{condition.majorCode}
and f.sub_system_code =#{condition.subSystemCode}
and f.sub_system_code in
#{item}
and f.`code` not in
#{item}

SELECT far.id, far.status, far.fault_analysis, far.solution, far.file_path, ft.name, far.fault_knowledge_base_id FROM fault_analysis_report far LEFT JOIN fault f ON f.CODE = far.fault_code LEFT JOIN fault_type ft on ft.`code` = f.fault_type_code WHERE 1=1
and far.id = #{id}
and far.fault_code = #{faultCode}
