select * from fault
status > 2
and date_format(approval_pass_time ,'%Y-%m-%d') >= date_format(#{startDate},'%Y-%m-%d') and date_format(approval_pass_time ,'%Y-%m-%d') <= date_format(#{endDate},'%Y-%m-%d')
and line_code = #{lineCode}
and major_code in
#{item}

select * from fault
status = 12
and date_format(end_time ,'%Y-%m-%d') >= date_format(#{todayStartDate},'%Y-%m-%d') and date_format(end_time ,'%Y-%m-%d') <= date_format(#{todayEndDate},'%Y-%m-%d')
and line_code = #{lineCode}
and major_code in
#{item}

select * from fault
status = 12
and date_format(end_time ,'%Y-%m-%d') >= date_format(#{weekStartDate},'%Y-%m-%d') and date_format(end_time ,'%Y-%m-%d') <= date_format(#{weekEndDate},'%Y-%m-%d')
and line_code = #{lineCode}
and major_code in
#{item}

select * from fault
status > 2
and date_format(happen_time ,'%Y-%m-%d') >= date_format(#{todayStartDate},'%Y-%m-%d') and date_format(happen_time ,'%Y-%m-%d') <= date_format(#{todayEndDate},'%Y-%m-%d')
and line_code = #{lineCode}
and major_code in
#{item}

select * from fault
status > 2
and date_format(happen_time ,'%Y-%m-%d') >= date_format(#{weekStartDate},'%Y-%m-%d') and date_format(happen_time ,'%Y-%m-%d') <= date_format(#{weekEndDate},'%Y-%m-%d')
and line_code = #{lineCode}
and major_code in
#{item}

select count(*) from fault where 1=1 and approval_pass_time  is not null

    and DATE_FORMAT(approval_pass_time ,'%Y-%m-%d') >= DATE_FORMAT(#{condition.firstDay},'%Y-%m-%d')


    and DATE_FORMAT(approval_pass_time ,'%Y-%m-%d') <= DATE_FORMAT(#{condition.lastDay},'%Y-%m-%d')


    and line_code  = #{condition.lineCode}


    and sub_system_code  = #{condition.subSystemCode}


    and fault_mode_code  = #{condition.faultModeCode}


    and status  = #{condition.status}


    and major_code in

        #{item}

SELECT system_code AS subSystemCode, system_name AS subSystemName FROM cs_subsystem WHERE del_flag = 0
and major_code in
#{item}

select cs.system_name, f.sub_system_code,f.line_code,
sum(TIMESTAMPDIFF(minute,f.happen_time,f.approval_pass_time)) as repairTime  from fault f
left join cs_subSystem cs on f.sub_system_code = cs.system_code

    f.status > 2

        and DATE_FORMAT(f.approval_pass_time,'%Y-%m') = #{month}


        and f.line_code  = #{lineCode}


        and f.major_code in

            #{item}



group by f.sub_system_code,f.line_Code

select * from fault

    status > 2

        and line_code  = #{lineCode}


        and major_code in

            #{item}

select * from fault


        AND status <> 12
        and TIMESTAMPDIFF(HOUR,happen_time,now()) >=#{lv1hours}


        AND status <> 12
        and TIMESTAMPDIFF(HOUR,happen_time,now()) >=#{lv2hours}
        and TIMESTAMPDIFF(HOUR,happen_time,now()) < #{lv1hours}


        AND status <> 12
        and TIMESTAMPDIFF(HOUR,happen_time,now()) >=#{lv3hours}
        and TIMESTAMPDIFF(HOUR,happen_time,now()) < #{lv2hours}


        and line_code  = #{lineCode}


        and date_format(approval_pass_time ,'%Y-%m-%d') >=  date_format(#{startDate},'%Y-%m-%d')
        and date_format(approval_pass_time ,'%Y-%m-%d') <= date_format(#{endDate},'%Y-%m-%d')


        and major_code in

            #{item}

select s.id as userId, s.realname, fr.id as taskId, fr.appoint_user_name, f.approval_pass_time, fr.recevice_time, fr.end_time, fr.assign_time, op.hang_up_time, IFNULL(TIMESTAMPDIFF( SECOND, IFNULL( fr.recevice_time, fr.assign_time ), fr.end_time ) - IFNUll( op.hang_up_time, '0' ),'0') AS duration from fault_repair_record fr left join sys_user s on s.username = fr.appoint_user_name left join fault f on f.code = fr.fault_code LEFT JOIN operation_process op ON op.fault_code = fr.fault_code
fr.del_flag = 0
AND f.approval_pass_time BETWEEN #{startTime} AND #{endTime}
and s.id in
#{item.id}
group by fr.id

select f.STATUS, fr.id as taskId, s.id as userid, s.realname, frp.user_name, f.approval_pass_time, fr.recevice_time, fr.end_time, fr.assign_time, op.hang_up_time, IFNULL(TIMESTAMPDIFF( SECOND, IFNULL( fr.recevice_time, fr.assign_time ), fr.end_time ) - IFNUll( op.hang_up_time, '0' ),'0') AS duration FROM fault_repair_participants frp LEFT JOIN sys_user s ON s.username = frp.user_name LEFT JOIN fault f ON f.CODE = frp.fault_code LEFT JOIN operation_process op ON op.fault_code = frp.fault_code left join fault_repair_record fr on fr.id = frp.fault_repair_record_id
fr.del_flag = 0
AND f.approval_pass_time BETWEEN #{startTime} AND #{endTime}
and s.id in
#{item.id}
group by fr.id

select sys_depart.id as orgId ,aaa.* from (SELECT IFNULL(sum(TIMESTAMPDIFF(second,IFNULL(fr.recevice_time,fr.assign_time),fr.end_time))-(SELECT SUM(IFNUll(op.hang_up_time,0)) FROM operation_process op WHERE op.fault_code = f.`code`),0) as num, count(fr.id) as num1 from sys_depart sd left join sys_user s on s.org_code = sd.org_code left join fault_repair_record fr on s.username = fr.appoint_user_name and fr.del_flag = 0 left join fault f on f.code = fr.fault_code where sd.del_flag =0
and sd.id = #{orgId}
AND date_format(f.approval_pass_time ,'%Y-%m-%d') BETWEEN date_format(#{startTime} ,'%Y-%m-%d') AND date_format(#{endTime} ,'%Y-%m-%d') )as aaa, sys_depart WHERE sys_depart.del_flag =0
and sys_depart.id= #{orgId}

SELECT sys_user.id as userId, ( SELECT IFNULL(sum(TIMESTAMPDIFF(second,IFNULL(fr.recevice_time,fr.assign_time),fr.end_time))-(SELECT SUM(IFNUll(op.hang_up_time,0)) FROM operation_process op WHERE op.fault_code = f.`code`),0) as num FROM sys_user su left join fault_repair_record fr on su.username = fr.appoint_user_name left join fault f on f.code = fr.fault_code where su.del_flag = 0
AND su.id = #{userId}
AND date_format(f.approval_pass_time ,'%Y-%m-%d') BETWEEN date_format( #{startTime} ,'%Y-%m-%d') AND date_format( #{endTime} ,'%Y-%m-%d') ) as num from sys_user where sys_user.del_flag = 0
AND sys_user.id = #{userId}
