select * from fault
AND status <> 12 and TIMESTAMPDIFF(HOUR,happen_time,now()) >=48
AND status <> 12 and TIMESTAMPDIFF(HOUR,happen_time,now()) >=24 and TIMESTAMPDIFF(HOUR,happen_time,now()) < 48
AND status <> 12 and TIMESTAMPDIFF(HOUR,happen_time,now()) >=12 and TIMESTAMPDIFF(HOUR,happen_time,now()) < 24
and line_code = #{lineCode}
and approval_pass_time >= #{startDate} and approval_pass_time <= #{endDate}
and major_code in
#{item}

select s.id,s.realname, fr.appoint_user_name,f.approval_pass_time,fr.recevice_time,fr.end_time,fr.assign_time, sum(TIMESTAMPDIFF(second,IFNULL(fr.recevice_time,fr.assign_time),fr.end_time)) as duration from fault_repair_record fr left join sys_user s on s.username = fr.appoint_user_name left join fault f on f.code = fr.fault_code
AND f.approval_pass_time BETWEEN #{startTime} AND #{endTime}
group by fr.appoint_user_name
