mapper.select:
SELECT su.realname FROM schedule_record sr LEFT JOIN sys_user su ON sr.user_id = su.id WHERE sr.del_flag = 0 AND DATE_FORMAT(sr.date ,'%Y-%m-%d') = DATE_FORMAT(#{today},'%Y-%m-%d')
and sr.user_id in
#{item.id}

select a.id, a.appoint_user_name, su.realname as appoint_real_name, if(a.recevice_time is null, a.assign_time, a.recevice_time) as recevice_time, a.start_time, a.end_time from fault_repair_record a LEFT JOIN fault f ON f.`code` = a.fault_code LEFT JOIN sys_user su on a.appoint_user_name = su.username where 1=1 AND DATE_FORMAT(f.approval_pass_time ,'%Y-%m-%d') >= DATE_FORMAT(#{beginDate},'%Y-%m-%d') AND DATE_FORMAT(f.approval_pass_time ,'%Y-%m-%d') <= DATE_FORMAT(#{endDate},'%Y-%m-%d')
AND su.id in
#{item.id}

SELECT rt.id as taskId, TIMESTAMPDIFF( SECOND, rt.begin_time, IF ( 1 = rt.is_receipt, rt.receipt_time, IF ( 1 = rt.is_confirm, rt.confirm_time, rt.submit_time ) ) ) inspecitonTotalTime FROM repair_pool rp LEFT JOIN repair_task rt ON rp.`code` = rt.`code` LEFT JOIN repair_task_user rts ON rts.repair_task_code = rt.`code` where rt.status = 8 AND rt.del_flag = 0 AND DATE_FORMAT(rp.start_time ,'%Y-%m-%d') >= DATE_FORMAT(#{beginDate},'%Y-%m-%d') AND DATE_FORMAT(rp.end_time ,'%Y-%m-%d') <= DATE_FORMAT(#{endDate},'%Y-%m-%d') AND rts.user_id in
#{item.id}
group by rt.id

SELECT rt.id as taskId, TIMESTAMPDIFF( SECOND, rt.begin_time, IF ( 1 = rt.is_receipt, rt.receipt_time, IF ( 1 = rt.is_confirm, rt.confirm_time, rt.submit_time ) ) ) inspecitonTotalTime FROM repair_pool rp LEFT JOIN repair_task rt ON rp.`code` = rt.`code` WHERE rt.STATUS = 8 AND rt.del_flag = 0 AND DATE_FORMAT(rp.start_time ,'%Y-%m-%d') >= DATE_FORMAT(#{beginDate},'%Y-%m-%d') AND DATE_FORMAT(rp.end_time ,'%Y-%m-%d') <= DATE_FORMAT(#{endDate},'%Y-%m-%d') AND rt.id IN ( SELECT rtdr.repair_task_id FROM repair_task_peer_rel rtpr LEFT JOIN repair_task_device_rel rtdr ON rtpr.repair_task_device_code = rtdr.`code` AND rtpr.user_id in
#{item.id}
GROUP BY rtdr.repair_task_id ) GROUP BY rt.id

SELECT su.id as userId, su.realname, su.avatar, su.job_grade, su.working_time, ( SELECT GROUP_CONCAT( sr.role_name ) FROM sys_role sr LEFT JOIN sys_user_role sur ON sr.id = sur.role_id WHERE sur.user_id = su.id ) AS roleName FROM sys_user su
WHERE su.org_id = #{teamId}
AND su.del_flag = 0 AND su.`status` = 1 GROUP BY su.id
