mapper.select:
SELECT * FROM (SELECT rpsr.station_code AS stationCode, cs.station_name AS stationName, cs.line_name AS lineName, MIN(rp.`status`) AS minStatus, CASE WHEN MIN(rp.`status`) = 8 THEN '已完成' ELSE '未完成' END AS statusName, GROUP_CONCAT(rp.code) AS codeStr FROM repair_pool AS rp LEFT JOIN repair_pool_station_rel AS rpsr ON rpsr.repair_pool_code = rp.`code` LEFT JOIN cs_station AS cs ON cs.station_code = rpsr.station_code LEFT JOIN repair_pool_org_rel AS rpor ON rpor.repair_pool_code = rp.code LEFT JOIN repair_pool_rel AS rpr ON rpr.repair_pool_code = rp.code LEFT JOIN repair_pool_code AS rpc ON rpc.id = rpr.repair_pool_sta_id
rp.start_time IS NOT NULL
AND date_format(rp.start_time ,'%Y-%m-%d') >= date_format(#{taskDetailsReq.startTime},'%Y-%m-%d') AND date_format(rp.start_time ,'%Y-%m-%d') <= date_format(#{taskDetailsReq.endTime},'%Y-%m-%d')
AND cs.station_code = #{taskDetailsReq.stationCode}
AND cs.line_code = #{taskDetailsReq.lineCode}
AND rp.type = #{taskDetailsReq.maintenanceCycle}
AND ${filterConditions}
GROUP BY rpsr.station_code
HAVING MAX( rp.`status` ) = 8
HAVING MIN( rp.`status` ) <> 8
) temptable
temptable.minStatus =8
and temptable.minStatus <> 8

SELECT DISTINCT rp.code,rp.name,rp.weeks,rp.type,rp.status,rp.start_time,rp.end_time FROM repair_pool rp LEFT JOIN repair_pool_station_rel rpsr ON rpsr.repair_pool_code = rp.`code` LEFT JOIN repair_pool_org_rel AS rpor ON rpor.repair_pool_code = rp.code LEFT JOIN repair_pool_rel AS rpr ON rpr.repair_pool_code = rp.code LEFT JOIN repair_pool_code AS rpc ON rpc.id = rpr.repair_pool_sta_id
rpsr.station_code = #{taskDetailsReq.stationCode} and rp.start_time is not null
and rp.status = #{taskDetailsReq.status}
and rp.status = 8
and rp.status <> 8
and date_format(rp.start_time ,'%Y-%m-%d') >= date_format(#{taskDetailsReq.startTime},'%Y-%m-%d') and date_format(rp.start_time ,'%Y-%m-%d') <= date_format(#{taskDetailsReq.endTime},'%Y-%m-%d')
and rp.type = #{taskDetailsReq.maintenanceCycle}
AND ${filterConditions}

SELECT rp.code,rp.name,rp.weeks,rp.type,rp.status,rp.start_time,rp.end_time FROM repair_pool rp LEFT JOIN repair_pool_station_rel rpsr ON rpsr.repair_pool_code = rp.`code`
rpsr.station_code = #{stationCode}
and date_format(rp.start_time ,'%Y-%m-%d') >= date_format(#{taskDetailsReq.startTime},'%Y-%m-%d') and date_format(rp.start_time ,'%Y-%m-%d') <= date_format(#{taskDetailsReq.endTime},'%Y-%m-%d')
