mapper.select:
SELECT rpsr.station_code AS stationCode, cs.station_name AS stationName, cs.line_name AS lineName FROM repair_pool rp LEFT JOIN repair_pool_station_rel rpsr ON rpsr.repair_pool_code = rp.`code` left join cs_station cs on cs.station_code = rpsr.station_code
rp.is_manual = 0
and rp.status = 8
and rp.status <> 8
and date_format(rp.start_time ,'%Y-%m-%d') >= date_format(#{taskDetailsReq.startTime},'%Y-%m-%d') and date_format(rp.start_time ,'%Y-%m-%d') <= date_format(#{taskDetailsReq.endTime},'%Y-%m-%d')
and cs.station_code = #{taskDetailsReq.stationCode}
and cs.line_code = #{taskDetailsReq.lineCode}
and cs.station_code in
#{code}
and rp.type = #{taskDetailsReq.maintenanceCycle}
GROUP BY rpsr.station_code

SELECT rp.code,rp.name,rp.weeks,rp.type,rp.status,rp.start_time,rp.end_time FROM repair_pool rp LEFT JOIN repair_pool_station_rel rpsr ON rpsr.repair_pool_code = rp.`code`
rp.is_manual = 0 and rpsr.station_code = #{taskDetailsReq.stationCode}
and status = #{taskDetailsReq.status}
and status = 8
and status <> 8
and date_format(rp.start_time ,'%Y-%m-%d') >= date_format(#{taskDetailsReq.startTime},'%Y-%m-%d') and date_format(rp.start_time ,'%Y-%m-%d') <= date_format(#{taskDetailsReq.endTime},'%Y-%m-%d')

SELECT rp.code,rp.name,rp.weeks,rp.type,rp.status,rp.start_time,rp.end_time FROM repair_pool rp LEFT JOIN repair_pool_station_rel rpsr ON rpsr.repair_pool_code = rp.`code`
rp.is_manual = 0 and rpsr.station_code = #{stationCode}
and date_format(rp.start_time ,'%Y-%m-%d') >= date_format(#{taskDetailsReq.startTime},'%Y-%m-%d') and date_format(rp.start_time ,'%Y-%m-%d') <= date_format(#{taskDetailsReq.endTime},'%Y-%m-%d')
