SELECT count( DISTINCT temp.id ) AS sum, sum( CASE WHEN temp.`status` = 8 THEN 1 ELSE 0 END ) AS finish, sum( CASE WHEN temp.`status` = 8 THEN 0 ELSE 1 END ) AS unfinish FROM ( SELECT DISTINCT rp.id,rp.CODE,rp.`status` FROM repair_pool rp LEFT JOIN repair_pool_org_rel rpor ON rpor.repair_pool_code = rp.CODE
rp.del_flag = 0
and rpor.org_code = #{orgCode}
and date_format(rp.start_time ,'%Y-%m-%d') >= date_format(#{beginDate} ,'%Y-%m-%d') and date_format(rp.start_time ,'%Y-%m-%d') <= date_format(#{endDate} ,'%Y-%m-%d')
) temp

select  org_code as list,repair_pool_code as code from  repair_pool_org_rel where del_flag = 0

