mapper.update:
update  inspection_code_content a
set a.del_flag =1
where a.pid=#{id}

mapper.select:
select * from inspection_code_content a where a.del_flag =0
and a.inspection_code_id=#{inspectionCodeContent.inspectionCodeId}
and a.`code` LIKE CONCAT ('%', #{inspectionCodeContent.code}, '%')
and a.`name` LIKE CONCAT ('%', #{inspectionCodeContent.name}, '%')
and a.status_item=#{inspectionCodeContent.statusItem}
ORDER BY a.sort_no ,a.create_time desc

select * from inspection_code_content a where a.del_flag =0
and a.inspection_code_id=#{id}
ORDER BY a.sort_no ,a.create_time desc
