mapper.select:
select id
from repair_task_device_rel
where code = #{code}

select realname
from sys_user
where id = #{staffId}

select station_code
from device
where code = #{deviceCode}

select rt.staff_id, rt.task_standard_rel_id, rt.device_code, rt.station_code from repair_task_device_rel rt where DATE_FORMAT(rt.submit_time, '%Y-%m-%d') = DATE_FORMAT(#{date}, '%Y-%m-%d') and rt.is_submit = 1
and rt.repair_task_id = #{taskId}
and rt.code = #{taskDeviceCode}
