mapper.select:
SELECT t1.*, ( SELECT group_concat( t2.org_code ) FROM repair_task_org_rel AS t2 WHERE t1.`code` = t2.repair_task_code ) AS orgCode, ( SELECT group_concat( t4.station_code ) FROM repair_task_station_rel AS t4 WHERE t1.`code` = t4.repair_task_code ) AS siteCode, ( SELECT group_concat( t6.major_code ) FROM repair_task_standard_rel AS t6 WHERE t1.id = t6.repair_task_id ) AS majorCode, ( SELECT group_concat( t6.subsystem_code ) FROM repair_task_standard_rel AS t6 WHERE t1.id = t6.repair_task_id ) AS systemCode, t8.start_time as startOverhaulTime, t8.end_time as endOverhaulTime, t8.device_code as equipmentCode FROM repair_task t1 LEFT JOIN repair_task_org_rel t2 ON t1.`code` = t2.repair_task_code LEFT JOIN repair_task_station_rel t4 ON t1.`code` = t4.repair_task_code LEFT JOIN repair_task_standard_rel t6 ON t1.id=t6.repair_task_id LEFT JOIN repair_task_device_rel t8 ON t1.id = t8.repair_task_id
t1.del_flag =0
and t1.id = #{condition.id}
and t1.code LIKE CONCAT ('%', #{condition.code}, '%')
and t1.type = #{condition.type}
and t1.status = #{condition.status}
and t2.org_code = #{condition.orgCode}
and t4.station_code = #{condition.siteCode}
and t1.work_type = #{condition.workType}
and DATE_FORMAT(t1.start_time, '%Y-%m-%d') >= DATE_FORMAT(#{condition.time1}, '%Y-%m-%d') and DATE_FORMAT(t1.end_time, '%Y-%m-%d') <= DATE_FORMAT(#{condition.time2}, '%Y-%m-%d')
and DATE_FORMAT(t1.start_time, '%Y-%m-%d') >= DATE_FORMAT(#{condition.weekStartTime}, '%Y-%m-%d') and DATE_FORMAT(t1.start_time, '%Y-%m-%d') <= DATE_FORMAT(#{condition.weekEndTime}, '%Y-%m-%d')
group by t1.id order by t1.create_time desc,t1.start_time desc

SELECT t1.id as taskId, t1.code as taskCode, t2.id as deviceId, t3.id as standardId, t2.code as overhaulCode, t3.major_code as majorCode, t3.subsystem_code as systemCode, t3.device_type_code as deviceTypeCode, t2.device_code as equipmentCode, t2.submit_time as submitTime, t2.start_time as startTime, t2.is_submit as isSubmit, t2.staff_id as overhaulId, t3.title as overhaulStandardName, t3.is_appoint_device as isAppointDevice, t1.status as maintenanceTaskStatus FROM repair_task t1 LEFT JOIN repair_task_device_rel t2 ON t1.id = t2.repair_task_id LEFT JOIN repair_task_standard_rel t3 ON t2.task_standard_rel_id = t3.id
t1.del_flag = 0 AND t2.del_flag = 0
AND t1.id = #{condition.taskId}
AND ( SELECT NAME FROM device WHERE CODE = t2.device_code ) LIKE CONCAT ('%', #{condition.equipmentName}, '%')
AND ( (( SELECT NAME FROM device WHERE CODE = t2.device_code ) LIKE CONCAT ('%', #{condition.search}, '%')) or (t2.device_code LIKE CONCAT ('%', #{condition.search}, '%')) or (t2.code LIKE CONCAT ('%', #{condition.search}, '%')) )
AND t2.device_code = #{condition.equipmentCode}
AND t3.major_code = #{condition.majorCode}
AND t3.subsystem_code = #{condition.systemCode}
AND t2.code = #{condition.overhaulCode}
AND t3.title = #{condition.overhaulStandardName}
AND t3.id = #{condition.standardId}
order by t2.update_time desc,t2.create_time desc

SELECT t1.id as taskId, t1.code as taskCode, t2.id as deviceId, t3.id as standardId, t2.code as overhaulCode, t2.duration as duration, t3.major_code as majorCode, t3.subsystem_code as systemCode, t3.device_type_code as deviceTypeCode, t2.device_code as equipmentCode, t2.submit_time as submitTime, t2.start_time as startTime, t2.is_submit as isSubmit, t2.staff_id as overhaulId, t3.title as overhaulStandardName, t3.is_appoint_device as isAppointDevice, t1.status as maintenanceTaskStatus FROM repair_task t1 LEFT JOIN repair_task_device_rel t2 ON t1.id = t2.repair_task_id LEFT JOIN repair_task_standard_rel t3 ON t2.task_standard_rel_id = t3.id LEFT JOIN repair_task_station_rel t4 ON t4.repair_task_code = t1.code
t1.del_flag = 0 AND t2.del_flag = 0
AND t1.id = #{taskId}
AND t4.station_code = #{stationCode}
order by t2.update_time desc,t2.create_time desc

SELECT rtdr.id as deviceId, rtdr.duration as duration, rtdr.device_code as equipmentCode, rtdr.start_time as startTime, rtdr.end_time as endTime, rtdr.line_code as lineCode, rtdr.position_code as positionCode, rtdr.specific_location as specificLocation, rtdr.staff_id as overhaulId, rtdr.code as resultCode, rtdr.fault_code as faultCode, rtdr.repair_task_id as taskId, rtdr.station_code as stationCode, rtdr.CODE as resultCode, rtsr.id as standardId, rtsr.major_code as majorCode, rtsr.subsystem_code as systemCode, rtsr.device_type_code as deviceTypeCode, rtdr.submit_time as submitTime, rtr.status as statusCode FROM repair_task_device_rel rtdr LEFT JOIN repair_task rt ON rt.id = rtdr.repair_task_id LEFT JOIN repair_task_standard_rel rtsr on rtsr.id = rtdr.task_standard_rel_id LEFT JOIN repair_task_result rtr on rtr.task_device_rel_id = rtdr.id
rtdr.del_flag = 0 AND rt.del_flag = 0
AND rtdr.repair_task_id = #{taskId}
AND rtdr.station_code = #{stationCode}
AND rtdr.id = #{deviceId}
group by rtdr.id

SELECT t1.id as taskId, t1.code as taskCode, t1.status as taskStatus, t2.id as deviceId, t3.id as standardId, t2.code as overhaulCode, t3.major_code as majorCode, t3.subsystem_code as systemCode, t3.device_type_code as deviceTypeCode, t2.device_code as equipmentCode, t2.submit_time as submitTime, t2.staff_id as overhaulId, t3.title as overhaulStandardName, t2.start_time, t2.duration, t2.is_submit as isSubmit, su.realname as overhaulName FROM repair_task t1 LEFT JOIN repair_task_device_rel t2 ON t1.id = t2.repair_task_id LEFT JOIN repair_task_standard_rel t3 ON t2.task_standard_rel_id = t3.id left join sys_user su on su.id = t2.staff_id
t1.del_flag = 0 AND t2.del_flag = 0 and t1.status = 8
AND t2.device_code = #{condition.equipmentCode}
AND t1.id = #{condition.taskId}
AND t3.major_code = #{condition.majorCode}
AND t3.subsystem_code = #{condition.systemCode}
AND t2.code like concat('%',#{condition.overhaulCode},'%')
AND t3.title like concat('%',#{condition.overhaulStandardName},'%')
AND su.realname like concat('%',#{condition.overhaulName},'%')
and DATE_FORMAT(t2.start_time, '%Y-%m-%d') >= DATE_FORMAT(#{condition.startTimeBegin}, '%Y-%m-%d') and DATE_FORMAT(t2.end_time, '%Y-%m-%d') <= DATE_FORMAT(#{condition.startTimeEnd}, '%Y-%m-%d')
order by t1.start_time desc

SELECT t1.id as taskId, t2.id as standardId, t2.title as overhaulStandardName, t2.major_code as majorCode, t2.subsystem_code as systemCode, t2.device_type_code as deviceTypeCode FROM repair_task t1 LEFT JOIN repair_task_standard_rel t2 ON t1.id = t2.repair_task_id
t1.del_flag = 0 and t1.id = #{id}
and t2.major_code = #{majorCode}
and t2.subsystem_code = #{subsystemCode}

SELECT * FROM repair_task_result
task_device_rel_id = #{id}
and status = #{status}
order by update_time desc,create_time desc

select year,code,type,weeks,start_time,end_time,status from repair_task where status = 8 and is_confirm = 0 and date_format(submit_time ,'%Y-%m-%d') =date_format(#{startDate} ,'%Y-%m-%d')
and code in
#{code}
union select year,code,type,weeks,start_time,end_time,status from repair_task where status = 8 and is_confirm = 1 and is_receipt =0 and date_format(confirm_time ,'%Y-%m-%d') =date_format(#{startDate} ,'%Y-%m-%d')
and code in
#{code}
union select year,code,type,weeks,start_time,end_time,status from repair_task where status = 8 and is_confirm = 1 and is_receipt =1 and date_format(receipt_time ,'%Y-%m-%d') =date_format(#{startDate} ,'%Y-%m-%d')
and code in
#{code}

select rt.submit_time as inspectionTime from repair_task rt
where  rt.is_receipt = 0 and rt.is_confirm =0 and rt.code = #{code}
union all
select rt.confirm_time as inspectionTime from repair_task rt
where  rt.is_receipt = 0 and rt.is_confirm =1 and rt.code = #{code}
union all
select rt.receipt_time as inspectionTime from repair_task rt
where  rt.is_receipt = 1 and rt.is_confirm =1 and rt.code = #{code}

SELECT t1.id as taskId, t2.id as id, t1.code as taskCode, ifnull( sum( TIMESTAMPDIFF( hour, t1.begin_time, IF ( 1 = t1.is_receipt, t1.receipt_time, IF ( 1 = t1.is_confirm, t1.confirm_time, t1.submit_time ) ) )),0)as maintenanceDuration, t1.start_time as startDate, t1.end_time as endDate, t2.org_code as orgCode, count(*)AS taskTotal FROM repair_pool t6 LEFT JOIN repair_task t1 ON t6.id = t1.repair_pool_id LEFT JOIN repair_task_org_rel t2 ON t1.`code`= t2.repair_task_code LEFT JOIN repair_task_station_rel t3 ON t1.`code`= t3.repair_task_code LEFT JOIN repair_task_standard_rel t4 ON t1.id= t4.repair_task_id
t1.del_flag =0 and t2.del_flag = 0 and t3.del_flag = 0 and t4.is_appoint_device = 0
and t3.line_code = #{condition.lineCode}
and t1.id = #{condition.taskId}
and t3.station_code = #{condition.stationCode}
and t2.org_code in
#{item}
and t2.org_code = #{condition.orgCode}
and t4.subsystem_code = #{condition.subsystemCode}
and DATE_FORMAT(t6.start_time ,'%Y-%m-%d') >= DATE_FORMAT(#{condition.startDate} ,'%Y-%m-%d') and DATE_FORMAT(t6.end_time ,'%Y-%m-%d') <= DATE_FORMAT(#{condition.endDate} ,'%Y-%m-%d')
and DATE_FORMAT(t6.start_time ,'%Y-%m-%d') <= DATE_FORMAT(#{condition.endDate} ,'%Y-%m-%d')
and t1.status = #{condition.status}
GROUP BY t2.org_code

SELECT t1.id as taskId, t2.id as id, t1.code as taskCode FROM repair_pool t6 LEFT JOIN repair_task t1 ON t6.id = t1.repair_pool_id LEFT JOIN repair_task_org_rel t2 ON t1.`code`= t2.repair_task_code
t1.del_flag =0 and t2.del_flag = 0
and t1.id = #{condition.taskId}
and t2.org_code = #{condition.orgCode}
and DATE_FORMAT(t6.start_time ,'%Y-%m-%d') >= DATE_FORMAT(#{condition.startDate} ,'%Y-%m-%d') and DATE_FORMAT(t6.end_time ,'%Y-%m-%d') <= DATE_FORMAT(#{condition.endDate} ,'%Y-%m-%d')
and DATE_FORMAT(t6.start_time ,'%Y-%m-%d') <= DATE_FORMAT(#{condition.endDate} ,'%Y-%m-%d')
and t1.status = #{condition.status}

SELECT t1.id as taskId, t2.id as id, t1.code as taskCode, ifnull( sum( TIMESTAMPDIFF( hour, t1.begin_time, IF ( 1 = t1.is_receipt, t1.receipt_time, IF ( 1 = t1.is_confirm, t1.confirm_time, t1.submit_time ) ) )),0)as maintenanceDuration, t1.start_time as startDate, t1.end_time as endDate, t2.user_id as userId, count(*)AS taskTotal FROM repair_pool t6 LEFT JOIN repair_task t1 ON t6.id = t1.repair_pool_id LEFT JOIN repair_task_user t2 ON t1.`code`= t2.repair_task_code LEFT JOIN repair_task_org_rel t3 ON t1.`code`= t3.repair_task_code LEFT JOIN repair_task_station_rel t4 ON t1.`code`= t4.repair_task_code LEFT JOIN repair_task_standard_rel t5 ON t1.id= t5.repair_task_id
t1.del_flag =0 and t2.del_flag = 0 and t3.del_flag = 0 and t4.del_flag = 0 and t5.is_appoint_device = 0
and t4.line_code = #{condition.lineCode}
and t1.id = #{condition.taskId}
and t4.station_code = #{condition.stationCode}
and t3.org_code in
#{item}
and t3.org_code = #{condition.orgCode}
and t5.subsystem_code = #{condition.subsystemCode}
and DATE_FORMAT(t6.start_time ,'%Y-%m-%d') >= DATE_FORMAT(#{condition.startDate} ,'%Y-%m-%d') and DATE_FORMAT(t6.end_time ,'%Y-%m-%d') <= DATE_FORMAT(#{condition.endDate} ,'%Y-%m-%d')
and DATE_FORMAT(t6.start_time ,'%Y-%m-%d') <= DATE_FORMAT(#{condition.endDate} ,'%Y-%m-%d')
and t1.status = #{condition.status}
GROUP BY t2.user_id

SELECT t1.id as taskId, t2.id as id, t2.user_id as userId FROM repair_pool t6 LEFT JOIN repair_task t1 ON t6.id = t1.repair_pool_id LEFT JOIN repair_task_user t2 ON t1.`code`= t2.repair_task_code LEFT JOIN repair_task_org_rel t3 ON t1.`code`= t3.repair_task_code
t1.del_flag =0 and t2.del_flag = 0 and t3.del_flag = 0
and t1.id = #{condition.taskId}
and t3.org_code = #{condition.orgCode}
and t2.user_id = #{condition.userId}
and DATE_FORMAT(t6.start_time ,'%Y-%m-%d') >= DATE_FORMAT(#{condition.startDate} ,'%Y-%m-%d') and DATE_FORMAT(t6.end_time ,'%Y-%m-%d') <= DATE_FORMAT(#{condition.endDate} ,'%Y-%m-%d')
and DATE_FORMAT(t6.start_time ,'%Y-%m-%d') <= DATE_FORMAT(#{condition.endDate} ,'%Y-%m-%d')
and t1.status = #{condition.status}
