mapper.select:
SELECT t1.*, ( SELECT group_concat( t2.org_code ) FROM repair_task_org_rel AS t2 WHERE t1.`code` = t2.repair_task_code ) AS orgCode, ( SELECT group_concat( t4.repair_task_code ) FROM repair_task_station_rel AS t4 WHERE t1.`code` = t4.repair_task_code ) AS siteCode, ( SELECT group_concat( t6.major_code ) FROM repair_task_standard_rel AS t6 WHERE t1.id = t6.repair_task_id ) AS majorCode, ( SELECT group_concat( t6.subsystem_code ) FROM repair_task_standard_rel AS t6 WHERE t1.id = t6.repair_task_id ) AS systemCode, t8.start_time as startOverhaulTime, t8.end_time as endOverhaulTime, t8.device_code as equipmentCode FROM repair_task t1 LEFT JOIN repair_task_org_rel t2 ON t1.`code` = t2.repair_task_code LEFT JOIN repair_task_station_rel t4 ON t1.`code` = t4.repair_task_code LEFT JOIN repair_task_standard_rel t6 ON t1.id=t6.repair_task_id LEFT JOIN repair_task_device_rel t8 ON t1.id = t8.repair_task_id
t1.del_flag =0
and t1.id = #{condition.id}
and t1.code = #{condition.code}
and t1.type = #{condition.type}
and t1.weeks = #{condition.weeks}
and t1.status = #{condition.status}
and t2.org_code = #{condition.orgCode}
and t4.station_code = #{condition.siteCode}
and t1.work_type = #{condition.workType}
and DATE_FORMAT(t1.start_time, '%Y-%m-%d') >= DATE_FORMAT(#{condition.time1}, '%Y-%m-%d') and DATE_FORMAT(t1.start_time, '%Y-%m-%d') <= DATE_FORMAT(#{condition.time2}, '%Y-%m-%d')
group by t1.id order by t1.start_time desc

SELECT t1.id as taskId, t1.code as taskCode, t1.status as taskStatus, t2.id as deviceId, t3.id as standardId, t2.code as overhaulCode, t3.major_code as majorCode, t3.subsystem_code as systemCode, t3.device_type_code as deviceTypeCode, t2.device_code as equipmentCode, t2.submit_time as submitTime, t2.start_time as startTime, t2.is_submit as isSubmit, t2.staff_id as overhaulId, t3.title as overhaulStandardName, t3.is_appoint_device as isAppointDevice FROM repair_task t1 LEFT JOIN repair_task_device_rel t2 ON t1.id = t2.repair_task_id LEFT JOIN repair_task_standard_rel t3 ON t2.task_standard_rel_id = t3.id
t1.del_flag = 0 AND t1.id = #{condition.taskId}
AND ( SELECT NAME FROM device WHERE CODE = t2.device_code ) LIKE CONCAT ('%', #{condition.equipmentName}, '%')
AND t2.device_code = #{condition.equipmentCode}
AND t3.major_code = #{condition.majorCode}
AND t3.subsystem_code = #{condition.systemCode}
AND t2.code = #{condition.overhaulCode}
AND t3.title = #{condition.overhaulStandardName}
order by t2.update_time desc,t2.create_time desc

SELECT t1.id as taskId, t1.code as taskCode, t1.status as taskStatus, t2.id as deviceId, t3.id as standardId, t2.code as overhaulCode, t3.major_code as majorCode, t3.subsystem_code as systemCode, t3.device_type_code as deviceTypeCode, t2.device_code as equipmentCode, t2.submit_time as submitTime, t2.staff_id as overhaulId, t3.title as overhaulStandardName, t2.start_time, t2.duration, t2.is_submit as isSubmit, su.realname as overhaulName FROM repair_task t1 LEFT JOIN repair_task_device_rel t2 ON t1.id = t2.repair_task_id LEFT JOIN repair_task_standard_rel t3 ON t2.task_standard_rel_id = t3.id left join sys_user su on su.id = t2.staff_id
t1.del_flag = 0 and t1.status = 8
AND ( SELECT NAME FROM device WHERE CODE = t3.device_type_code ) LIKE CONCAT ('%', #{condition.equipmentName}, '%') AND t2.device_code = #{condition.equipmentCode}
AND t1.id = #{condition.taskId}
AND t3.major_code = #{condition.majorCode}
AND t3.subsystem_code = #{condition.systemCode}
AND t2.code like concat('%',#{condition.overhaulCode},'%')
AND t3.title like concat('%',#{condition.overhaulStandardName},'%')
AND su.realname like concat('%',#{condition.overhaulName},'%')
and DATE_FORMAT(t2.start_time, '%Y-%m-%d') >= DATE_FORMAT(#{condition.startTimeBegin}, '%Y-%m-%d') and DATE_FORMAT(t2.start_time, '%Y-%m-%d') <= DATE_FORMAT(#{condition.startTimeEnd}, '%Y-%m-%d')
order by t1.start_time desc

SELECT t1.id as taskId, t2.id as standardId, t2.title as overhaulStandardName, t2.major_code as majorCode, t2.subsystem_code as systemCode, t2.device_type_code as deviceTypeCode FROM repair_task t1 LEFT JOIN repair_task_standard_rel t2 ON t1.id = t2.repair_task_id
t1.del_flag = 0 and t1.id = #{id}
and t2.major_code = #{majorCode}
and t2.subsystem_code = #{subsystemCode}

SELECT * FROM repair_task_result
task_device_rel_id = #{id}
and status = #{status}
order by update_time desc,create_time desc
