mapper.select:
SELECT pts.profession_code majorCode, cm.major_name majorName, pts.subsystem_code, cs.system_name subsystemName, pts.device_type_code, dt.`name` deviceTypeName, ps.`name` standardName, device.`name` deviceName, su.realname username, ptd.* FROM patrol_task_device ptd JOIN patrol_task_standard pts ON ptd.task_standard_id = pts.id AND pts.del_flag = 0 LEFT JOIN patrol_standard ps ON pts.standard_id = ps.id AND ps.del_flag = 0 LEFT JOIN device ON ptd.device_code = device.`code` AND device.del_flag = 0 LEFT JOIN cs_major cm ON pts.profession_code = cm.major_code AND cm.del_flag = 0 LEFT JOIN cs_subsystem cs ON pts.subsystem_code = cs.system_code AND cs.del_flag = 0 LEFT JOIN device_type dt ON pts.device_type_code = dt.`code` AND dt.del_flag = 0 LEFT JOIN sys_user su ON ptd.user_id = su.id AND su.del_flag = 0 WHERE ptd.del_flag = 0
and ptd.task_id = #{taskDevice.taskId}
and device.`name` like concat('%', #{taskDevice.deviceName}, '%')
and ptd.check_result = #{taskDevice.checkResult}
and ptd.patrol_number like concat('%', #{taskDevice.patrolNumber}, '%')
and su.realname like concat('%', #{taskDevice.username}, '%')
and pts.profession_code = #{taskDevice.majorCode}
and pts.subsystem_code = #{taskDevice.subsystemCode}

SELECT pts.profession_code majorCode, cm.major_name majorName, pts.subsystem_code, cs.system_name subsystemName, pts.device_type_code, dt.`name` deviceTypeName, ps.`name` standardName, device.`name` deviceName, su.realname username, ptd.* FROM patrol_task_device ptd JOIN patrol_task_standard pts ON ptd.task_standard_id = pts.id LEFT JOIN patrol_standard ps ON pts.standard_id = ps.id LEFT JOIN device ON ptd.device_code = device.`code` LEFT JOIN cs_major cm ON pts.profession_code = cm.major_code LEFT JOIN cs_subsystem cs ON pts.subsystem_code = cs.system_code LEFT JOIN device_type dt ON pts.device_type_code = dt.`code` LEFT JOIN sys_user su ON ptd.user_id = su.id WHERE ptd.del_flag = 0 and ptd.status = 2
and ptd.task_id = #{taskDevice.taskId}
and ptd.device_code = #{taskDevice.deviceCode}
and device.`name` like concat('%', #{taskDevice.deviceName}, '%')
and ptd.check_result = #{taskDevice.checkResult}
and ptd.patrol_number like concat('%', #{taskDevice.patrolNumber}, '%')
and su.realname like concat('%', #{taskDevice.username}, '%')
and ps.`name` like concat('%', #{taskDevice.standardName}, '%')
and DATE_FORMAT(ptd.start_time, '%Y-%m-%d') >= DATE_FORMAT(#{taskDevice.startTimeBegin}, '%Y-%m-%d') and DATE_FORMAT(ptd.start_time, '%Y-%m-%d') <= DATE_FORMAT(#{taskDevice.startTimeEnd}, '%Y-%m-%d')

select realname
from sys_user
where id = #{userId}

select t1.*
from device t1
where t1.code = #{code}

select t1.position_name
from cs_station_position t1
where t1.position_code = #{positionCode}

SELECT pt.* FROM patrol_task_device pt WHERE DATE_FORMAT(pt.check_time, '%Y-%m-%d') = DATE_FORMAT(#{date}, '%Y-%m-%d') AND pt.`status` = 2
and pt.task_id = #{taskId}
and pt.patrol_number = #{taskDeviceCode}
