mapper.select:
SELECT pt.* FROM patrol_task pt WHERE pt.del_flag = 0
and pt.name like concat('%',#{patrolTask.name},'%')
and pt.code like concat('%',#{patrolTask.code},'%')
and pt.status in
#{item}
and pt.status = #{patrolTask.status}
and pt.source = #{patrolTask.source}
and pt.discard_status = #{patrolTask.discardStatus}
and pt.code in ( SELECT ppo.task_code FROM patrol_task_organization ppo WHERE ppo.del_flag = 0 and ppo.organization_code = #{patrolTask.organizationCode} )
and pt.code in ( SELECT pps.task_code FROM patrol_task_station pps WHERE pps.del_flag = 0 and pps.station_code = #{patrolTask.stationCode} )
AND DATE_FORMAT( pt.patrol_date, '%Y-%m-%d' ) BETWEEN DATE_FORMAT( #{patrolTask.dateHead}, '%Y-%m-%d' ) AND DATE_FORMAT( #{patrolTask.dateEnd}, '%Y-%m-%d')
order by pt.create_time desc

select realname
from sys_user
where id = #{patrolReturnUserId}

SELECT t1.org_code
FROM patrol_task_organization t1
WHERE t1.task_code = #{taskCode}

select t2.depart_name
from sys_depart t2
where t2.org_code = #{code}

SELECT cs.system_code subsystemCode, cs.system_name subsystemName FROM cs_subsystem cs WHERE
cs.major_code = #{majorCode}
AND cs.system_code IN
#{item.subsystemCode}

select station_code
from patrol_task_station
where task_code =#{code}
