SELECT t1.* FROM ( SELECT t1.*, date_format( t1.create_time, '%Y-%m-%d' ) AS creatTime, GROUP_CONCAT( cl.line_name ) AS lineName, GROUP_CONCAT( DISTINCT cl.sort ) AS lineSort, SUBSTRING_INDEX( GROUP_CONCAT( DISTINCT cs.sort ), ',', 1 ) * 1 AS stationSort, ( SELECT realname FROM sys_user WHERE id = t1.end_user_id ) AS endUserName, concat( DATE_FORMAT( t1.start_time, '%H:%i' ), "-", DATE_FORMAT( t1.end_time, '%H:%i' )) AS startEndTime FROM patrol_task t1 LEFT JOIN patrol_task_station pts ON t1.`code` = pts.task_code LEFT JOIN cs_station cs ON pts.station_code = cs.station_code LEFT JOIN cs_line cl ON cs.line_code = cl.line_code WHERE t1.STATUS NOT IN ( 6, 7, 8 ) AND t1.del_flag = 0 AND t1.omit_status = 0 AND t1.discard_status = 0 AND cs.del_flag = 0 AND cl.del_flag = 0
AND cl.line_code = #{patrolTaskDTO.lineCode}
AND t1.id NOT IN ( SELECT id FROM patrol_task WHERE source = 3 AND ( `status` = 0 OR `status` = 3 )) GROUP BY t1.id ) AS t1 where 1=1
and DATE_FORMAT(t1.patrol_date, '%Y-%m-%d') between DATE_FORMAT(#{patrolTaskDTO.dateHead}, '%Y-%m-%d') and DATE_FORMAT(#{patrolTaskDTO.dateEnd}, '%Y-%m-%d')
and t1.CODE IN ( SELECT t2.task_code FROM patrol_task_organization t2 WHERE t2.task_code = t1.CODE AND t2.org_code in
#{item}
)
AND t1.code IN
#{item}
and t1.status = #{patrolTaskDTO.status}
AND t1.CODE IN (SELECT t2.task_code FROM patrol_task_organization t2 WHERE t2.task_code = t1.CODE AND t2.org_code = #{patrolTaskDTO.orgCode})
and t1.code in ( SELECT pps.task_code FROM patrol_task_station pps JOIN cs_station cs ON pps.station_code = cs.station_code AND cs.del_flag = 0 WHERE pps.del_flag = 0 AND cs.line_code = #{patrolTaskDTO.stationCode} )
and t1.code in ( SELECT pps.task_code FROM patrol_task_station pps WHERE pps.del_flag = 0 and pps.station_code = #{patrolTaskDTO.stationCode} )
ORDER BY t1.creatTime DESC, t1.lineSort ASC, t1.stationSort ASC;

select t1.*, (select realname from sys_user where id = t1.end_user_id) as endUserName, concat(DATE_FORMAT(t1.start_time, '%H:%i'), "-", DATE_FORMAT(t1.end_time, '%H:%i')) as startEndTime from patrol_task t1 where t1.status in (6, 7) and t1.del_flag = 0 and t1.omit_status = 0 and t1.discard_status = 0
and t1.CODE IN ( SELECT t2.task_code FROM patrol_task_organization t2 WHERE t2.task_code = t1.CODE AND t2.org_code in
#{item}
)
and DATE_FORMAT(t1.patrol_date, '%Y-%m-%d') between DATE_FORMAT(#{patrolTaskDTO.dateHead}, '%Y-%m-%d') and DATE_FORMAT(#{patrolTaskDTO.dateEnd}, '%Y-%m-%d')
and t1.status = #{patrolTaskDTO.status}
and t1.code in ( SELECT pps.task_code FROM patrol_task_station pps JOIN cs_station cs ON pps.station_code = cs.station_code AND cs.del_flag = 0 WHERE pps.del_flag = 0 AND cs.line_code = #{patrolTaskDTO.stationCode} )
and t1.code in ( SELECT pps.task_code FROM patrol_task_station pps WHERE pps.del_flag = 0 and pps.station_code = #{patrolTaskDTO.stationCode} )
and t1.code in
#{item}
ORDER BY t1.`status` asc, t1.create_time desc

