and pt.omit_status = #{patrolTask.omitStatus}
and pt.source = #{patrolTask.source}
and pt.discard_status = #{patrolTask.discardStatus}
and pt.code in ( SELECT ppo.task_code FROM patrol_task_organization ppo WHERE ppo.del_flag = 0 and ppo.org_code = #{patrolTask.organizationCode} )
and pt.code in ( SELECT pps.task_code FROM patrol_task_station pps JOIN cs_station cs ON pps.station_code = cs.station_code AND cs.del_flag = 0 WHERE pps.del_flag = 0 AND cs.line_code = #{patrolTask.stationCode} )
and pt.code in ( SELECT pps.task_code FROM patrol_task_station pps WHERE pps.del_flag = 0 and pps.station_code = #{patrolTask.stationCode} )
AND DATE_FORMAT(pt.patrol_date, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{patrolTask.dateHead}, '%Y-%m-%d') AND DATE_FORMAT(#{patrolTask.dateEnd}, '%Y-%m-%d')
AND DATE_FORMAT(pt.submit_time, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{patrolTask.submitDateHead}, '%Y-%m-%d') AND DATE_FORMAT(#{patrolTask.submitDateEnd}, '%Y-%m-%d')
AND DATE_FORMAT(pt.patrol_date, '%Y-%m-%d') = DATE_FORMAT(#{patrolTask.patrolDate}, '%Y-%m-%d')
and pt.code in
#{item}
order by
pt.`status` asc,
pt.create_time desc

