SELECT sub.* FROM ( SELECT pts.station_code, cs.station_name, cs.line_code, cs.line_name, GROUP_CONCAT(pt.`code`) taskCode, IF(MIN(pt.`status`) = 7, '已完成', '未完成') statusName, MAX(pt.submit_time) submitTime
, IF(GROUP_CONCAT(DISTINCT pt.`status`) REGEXP #{regexp}, 1, 0) `status`
FROM patrol_task pt JOIN patrol_task_station pts ON pt.`code` = pts.task_code JOIN cs_station cs ON pts.station_code = cs.station_code WHERE pt.del_flag = 0
AND DATE_FORMAT(pt.patrol_date, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{condition.startDate}, '%Y-%m-%d') AND DATE_FORMAT(#{condition.endDate}, '%Y-%m-%d')
AND pt.`code` in
#{item.taskCode}
AND pt.`code` in
#{item.taskCode}
AND pt.id in
#{item.taskId}
AND pt.omit_status = #{condition.omitStatus}
AND pts.station_code = #{condition.stationCode}
AND cs.line_code = #{condition.lineCode}
GROUP BY pts.station_code
HAVING MAX(pt.`status`) = 7
HAVING MIN( pt.`status` ) <> 7
) sub
where sub.`status` = #{condition.finishStatus}

