SELECT pt.`name`, pt.`code`, pt.`status`, pt.patrol_date, IF(1 = pt.auditor, pt.auditor_time, pt.submit_time) submitTime, GROUP_CONCAT(DISTINCT ptu.user_name SEPARATOR '；' ) userInfo, GROUP_CONCAT(DISTINCT cs.station_name SEPARATOR '；' ) stationInfo, GROUP_CONCAT(DISTINCT sd.depart_name SEPARATOR '；' ) orgInfo FROM patrol_task pt LEFT JOIN patrol_task_user ptu ON pt.`code` = ptu.task_code AND ptu.del_flag = 0 LEFT JOIN patrol_task_station pts ON pt.`code` = pts.task_code AND pts.del_flag = 0 LEFT JOIN cs_station cs ON pts.station_code = cs.station_code AND cs.del_flag = 0 LEFT JOIN patrol_task_organization pto ON pt.`code` = pto.task_code AND pto.del_flag = 0 LEFT JOIN sys_depart sd ON pto.org_code = sd.org_code AND sd.del_flag = 0 LEFT JOIN patrol_task_standard pts2 ON pt.id = pts2.task_id AND pts2.del_flag = 0 WHERE pt.del_flag = 0
AND DATE_FORMAT(pt.patrol_date, '%Y-%m-%d') = DATE_FORMAT(#{condition.date}, '%Y-%m-%d')
and pt.source = #{condition.source}
and pt.`status` = #{condition.status}
and pt.`code` in ( SELECT pps.task_code FROM patrol_task_station pps WHERE pps.del_flag = 0 and pps.station_code = #{condition.stationCode} )
GROUP BY pt.`code`

