SELECT a.id, a.system_name, a.system_code as code, count(c.id) AS failureNum, count(c.id AND c.fault_mode_code = 1 OR NOT NULL) AS repairNum, count(c.id AND c.fault_mode_code = 0 OR NOT NULL) AS selfInspectionNum, count(c.id AND c.end_time is NOT NULL and c.status = 12 OR not null) AS resolvedNum, count(c.id AND c.status <> 12 OR not NULL) AS unResolvedNum, (SELECT COUNT(id) FROM fault WHERE a.system_code = sub_system_code and `status` > 2 AND DATE_FORMAT(happen_time, '%Y-%m') = DATE_FORMAT(#{endTime}, '%Y-%m')
AND line_code = #{lineCode}
AND station_code IN
#{item}
) as monthNum, (SELECT COUNT(id) FROM fault WHERE a.system_code = sub_system_code and `status` > 2 AND DATE_FORMAT(happen_time, '%Y-%m') = DATE_FORMAT(date_sub(#{endTime}, interval 1 month), '%Y-%m')
AND line_code = #{lineCode}
AND station_code IN
#{item}
) as lastMonthNum, (SELECT COUNT(id) FROM fault WHERE a.system_code = sub_system_code and `status` > 2 AND DATE_FORMAT(happen_time, '%Y') = DATE_FORMAT(curdate(), '%Y')
AND line_code = #{lineCode}
AND station_code IN
#{item}
) as yearNum, (SELECT COUNT(id) FROM fault WHERE a.system_code = sub_system_code and `status` > 0 AND DATE_FORMAT(happen_time, '%Y') = DATE_FORMAT(date_sub(curdate(), interval 1 year), '%Y')
AND line_code = #{lineCode}
AND station_code IN
#{item}
) as lastYearNum FROM cs_subsystem a LEFT JOIN fault c ON a.system_code = c.sub_system_code and c.`status` > 2
and DATE_FORMAT(c.happen_time, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{startTime}, '%Y-%m-%d') AND DATE_FORMAT(#{endTime}, '%Y-%m-%d')
AND c.line_code = #{lineCode}
AND c.station_code IN
#{item}
where a.del_flag = 0
AND a.id in (select system_id from cs_user_subsystem where user_id = #{id})
group by a.id

SELECT count(pt.id)        AS taskTotal,
       GROUP_CONCAT(pt.id) as taskId
        FROM patrol_task pt
        JOIN patrol_task_organization pto ON pt.`code` = pto.task_code

    AND pto.org_code = #{orgCode}


    AND DATE_FORMAT(pt.patrol_date, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{condition.startDate}, '%Y-%m-%d')
                AND DATE_FORMAT(#{condition.endDate}, '%Y-%m-%d')


    and pt.code in (
            SELECT pps.task_code
            FROM patrol_task_station pps
                         LEFT JOIN cs_station cs on pps.station_code = cs.station_code
            where pps.station_code = #{condition.stationCode}
              and cs.line_code = #{condition.lineCode}
            )


    and pt.code in (
            SELECT pps.task_code
            FROM patrol_task_station pps
                         LEFT JOIN cs_station cs on pps.station_code = cs.station_code
            where cs.line_code = #{condition.lineCode}
            )


    and pt.code in (
            SELECT pps.task_code
            FROM patrol_task_station pps
                         LEFT JOIN cs_station cs on pps.station_code = cs.station_code
            where pps.station_code = #{condition.stationCode}
            )


    and pt.id in (
            SELECT pts.task_id
            FROM patrol_task_standard pts
            where pts.subsystem_code = #{condition.subsystemCode}
            )

GROUP BY pto.org_code

