select ifnull((TIME_TO_SEC(a.time) / 60), 0) as times from (SELECT timediff(c.arrive_time, IFNULL(c.recevice_time, c.assign_time)) as time FROM fault a LEFT JOIN fault_repair_record c ON a.`code` = c.fault_code WHERE a.`status` >0
and a.sub_system_code = #{code}
and a.sys_org_code = #{orgCode}
and DATE_FORMAT( a.happen_time , '%Y-%m-%d' ) BETWEEN DATE_FORMAT( #{startTime} , '%Y-%m-%d' ) AND DATE_FORMAT( #{endTime} , '%Y-%m-%d' )
AND a.line_code = #{lineCode}
AND a.station_code IN
#{item}
and (a.end_time or not NULL) ) as a where 1 = 1

select ifnull((TIME_TO_SEC(abc.time) / 60), 0) as times from (SELECT timediff(c.end_time, IFNULL(c.recevice_time, c.assign_time)) as time FROM fault a LEFT JOIN fault_repair_record c ON a.`code` = c.fault_code WHERE a.`status` >0
and a.sub_system_code = #{code}
and a.sys_org_code = #{orgCode}
and DATE_FORMAT( a.happen_time , '%Y-%m-%d' ) BETWEEN DATE_FORMAT( #{startTime} , '%Y-%m-%d' ) AND DATE_FORMAT( #{endTime} , '%Y-%m-%d' )
AND a.line_code = #{lineCode}
AND a.station_code IN
#{item}
and (a.end_time or not NULL) ) as abc where 1 = 1

select a.system_name, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '01' or not NULL) as january, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '02' or not NULL) as february, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '03' or not NULL) as march, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '04' or not NULL) as april, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '05' or not NULL) as may, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '06' or not NULL) as june, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '07' or not NULL) as july, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '08' or not NULL) as august, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '09' or not NULL) as september, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '10' or not NULL) as october, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '11' or not NULL) as november, COUNT(c.id and DATE_FORMAT(c.happen_time, '%m') = '12' or not NULL) as december FROM cs_subsystem a LEFT JOIN fault c ON a.system_code = c.sub_system_code and c.`status` > 2 AND DATE_ADD(c.happen_time, INTERVAL 1 YEAR) > NOW()
AND c.line_code = #{lineCode}
AND c.station_code IN
#{item}
WHERE a.del_flag = 0 AND a.id in (select system_id from cs_user_subsystem where user_id = #{id}) GROUP BY a.system_name

