mapper.select:
select t1.* from patrol_task t1 where t1.status not in (6,7)
and DATE_FORMAT(t1.patrol_date, '%Y-%m-%d') = DATE_FORMAT(#{patrolTaskDTO.patrolDate}, '%Y-%m-%d')
and t1.status = #{patrolTaskDTO.status}

select t1.* from patrol_task t1 where t1.status in (6,7)
and DATE_FORMAT(t1.patrol_date, '%Y-%m-%d') = DATE_FORMAT(#{patrolTaskDTO.patrolDate}, '%Y-%m-%d')
and t1.status = #{patrolTaskDTO.status}

SELECT pt.* FROM patrol_task pt WHERE pt.del_flag = 0
and pt.name like concat('%',#{patrolTask.name},'%')
and pt.code like concat('%',#{patrolTask.code},'%')
and pt.status = #{patrolTask.status}
and pt.source = #{patrolTask.source}
and pt.plan_code in ( SELECT ppo.plan_code FROM patrol_plan_organization ppo WHERE ppo.organization_code = #{patrolTask.organizationCode} )

select realname
from sys_user
where id = #{patrolReturnUserId}

select t2.depart_name
from sys_depart t2
where t2.org_code = #{code}
