mapper.select:
select sd.depart_name from sys_user su left join sys_depart sd on su.org_id = sd.id and sd.del_flag = 0 where su.del_flag = 0 and sd.depart_name is not null
AND su.id IN
#{item}

SELECT ptu.user_id, sum( TIMESTAMPDIFF( SECOND, pt.begin_time, IF ( 1 = pt.auditor, pt.auditor_time, pt.submit_time ) ) ) duration FROM patrol_task pt JOIN patrol_task_user ptu ON pt.`code` = ptu.task_code AND ptu.del_flag = 0 WHERE pt.del_flag = 0
AND pt.patrol_date BETWEEN #{startTime} AND #{endTime}
GROUP BY ptu.user_id

SELECT total.user_id, sum( total.duration ) duration FROM ( SELECT pa.user_id, TIMESTAMPDIFF( SECOND, pt.begin_time, IF ( 1 = pt.auditor, pt.auditor_time, pt.submit_time ) ) duration FROM patrol_task pt JOIN patrol_task_device ptd ON pt.id = ptd.task_id AND ptd.del_flag = 0 JOIN patrol_accompany pa ON ptd.patrol_number = pa.task_device_code AND pt.del_flag = 0 WHERE pt.del_flag = 0
AND pt.patrol_date BETWEEN #{startTime} AND #{endTime}
GROUP BY pa.user_id, pt.id ) total GROUP BY total.user_id

SELECT pt.id as taskId, IFNULL(TIMESTAMPDIFF( SECOND, pt.begin_time, IF ( 1 = pt.auditor, pt.auditor_time, pt.submit_time ) ),'0') duration FROM patrol_task pt JOIN patrol_task_user ptu ON pt.`code` = ptu.task_code AND ptu.del_flag = 0 WHERE pt.del_flag = 0
AND pt.patrol_date BETWEEN #{startTime} AND #{endTime}
and ptu.user_id in
#{item.id}
GROUP BY pt.id

SELECT pt.id as taskId, IFNULL(TIMESTAMPDIFF( SECOND, pt.begin_time, IF ( 1 = pt.auditor, pt.auditor_time, pt.submit_time ) ),'0') duration FROM patrol_task pt JOIN patrol_task_device ptd ON pt.id = ptd.task_id AND ptd.del_flag = 0 JOIN patrol_accompany pa ON ptd.patrol_number = pa.task_device_code AND pt.del_flag = 0 WHERE pt.del_flag = 0
AND pt.patrol_date BETWEEN #{startTime} AND #{endTime}
and pa.user_id in
#{item.id}
GROUP BY pt.id

select year(pt.patrol_date) `year`, count(pt.id) `number`
from patrol_task_user ptu
    join patrol_task pt
on (ptu.task_code = pt.code and pt.del_flag = 0)
where ptu.del_flag = 0
  and year(pt.patrol_date) between #{fiveYearsAgo}
  and #{thisYear}
  and pt.status = #{taskComplete}
  and ptu.user_id = #{userId}
group by year(pt.patrol_date)
