mapper.select:
select sps.id,sps.material_code,mb.name materialName,mb.type, mb.specifications,mb.country_origin countryOrigin,mb.manufacturer, sps.num,sps.status,sps.reason,sps.create_time from spare_part_scrap sps left join material_base mb on mb.code=sps.material_code where 1=1 and sps.del_flag!=1
and mb.name like concat('%', #{sparePartLendQuery.materialName}, '%')
and mb.type = #{sparePartLendQuery.type}
and sps.status = #{sparePartScrapQuery.status}
order by sps.create_time desc

select sps.id,sps.material_code,mb.name materialName,mb.type, mb.specifications,mb.country_origin countryOrigin,mb.manufacturer, sps.num,sps.status,sps.reason,sps.create_time from spare_part_scrap sps left join material_base mb on mb.code=sps.material_code where 1=1 and sps.del_flag!=1
and mb.name like concat('%', #{sparePartLendQuery.materialName}, '%')
and mb.type = #{sparePartLendQuery.type}
and sps.status = #{sparePartScrapQuery.status}
order by sps.create_time desc
