mapper.select:
select stock_in_order_level2.*, mr.code as materialRequisitionCode from stock_in_order_level2 stock_in_order_level2 left join stock_level2_info sli on (stock_in_order_level2.warehouse_code = sli.warehouse_code and sli.del_flag = 0) left join material_requisition mr on mr.id = stock_in_order_level2.material_requisition_id
and stock_in_order_level2.del_flag = 0
and stock_in_order_level2.order_code like concat(concat('%',#{condition.orderCode}),'%')
and sli.warehouse_name like concat(concat('%',#{condition.warehouseName}),'%')
and stock_in_order_level2.warehouse_code = #{condition.warehouseCode}
and Date(stock_in_order_level2.entry_time) >= #{condition.entryTimeBegin} and Date(stock_in_order_level2.entry_time) <= #{condition.entryTimeEnd}
and sli.org_code in
#{item}
order by stock_in_order_level2.create_time desc

select siol2.* from stock_in_order_level2 siol2 left join stock_level2_info sli on (siol2.warehouse_code = sli.warehouse_code and sli.del_flag = 0)
and siol2.del_flag = 0
and siol2.id = #{condition.id}
and sli.warehouse_name like concat(concat('%',#{condition.warehouseName}),'%')
and siol2.warehouse_code = #{condition.warehouseCode}
order by siol2.create_time desc

select a.* from stock_incoming_materials a
where a.del_flag =0

    and a.in_order_code=#{orderCode}
